import axios from 'axios';

// Create axios instance with default config
const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  timeout: 60000, // Increase timeout to 60 seconds for large files
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

// Add request interceptor for logging
api.interceptors.request.use(
  config => {
    console.log('API Request:', config.method.toUpperCase(), config.url);
    return config;
  },
  error => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for error handling
api.interceptors.response.use(
  response => {
    console.log('API Response:', response.status);
    return response;
  },
  error => {
    console.error('API Response Error:', error);
    if (error.code === 'ERR_NETWORK') {
      throw new Error('لا يمكن الاتصال بالخادم. يرجى التأكد من تشغيل الخادم والمحاولة مرة أخرى');
    }
    throw error.response?.data?.message || error.message || 'حدث خطأ غير معروف';
  }
);

export const getVideoInfo = async (url) => {
  try {
    const response = await api.get('/video-info', {
      params: { url }
    });
    return response.data;
  } catch (error) {
    console.error('Error in getVideoInfo:', error);
    throw error;
  }
};

export const downloadVideo = async (url, itag) => {
  try {
    // First check if the format needs audio merging
    const info = await api.get('/video-info', {
      params: { url }
    });

    if (!info.data || !info.data.formats) {
      throw new Error('لم يتم العثور على معلومات الفيديو');
    }

    const format = info.data.formats.find(f => f.itag === parseInt(itag));
    if (!format) {
      throw new Error('تنسيق الفيديو المطلوب غير متوفر');
    }

    const needsAudioMerge = format.hasAudio === false;
    console.log('Format:', format);
    console.log('Needs audio merge:', needsAudioMerge);

    // Download with appropriate parameters
    const response = await api.get('/download', {
      params: {
        url,
        itag,
        includeAudio: needsAudioMerge
      },
      responseType: 'blob',
      timeout: 300000 // 5 minutes timeout for large files
    });

    // Create a blob URL and trigger download
    const blob = new Blob([response.data], { type: 'video/mp4' });
    const downloadUrl = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = downloadUrl;

    // Get video title for filename
    const videoTitle = (info.data.videoDetails?.title || 'video').replace(/[^\w\s]/gi, '');
    const qualityLabel = format.qualityLabel || `${itag}p`;
    link.download = `${videoTitle}_${qualityLabel}.mp4`;

    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(downloadUrl);
  } catch (error) {
    console.error('Error in downloadVideo:', error);
    throw new Error(error.message || 'فشل تحميل الفيديو. يرجى المحاولة مرة أخرى');
  }
};
