import React from 'react';
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Typography,
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import { formatFileSize } from '../utils/format';

const DownloadOptions = ({ formats, onDownload }) => {
  if (!formats || formats.length === 0) return null;

  const filteredFormats = formats.filter(
    format => format.hasVideo && format.quality
  );

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Quality</TableCell>
            <TableCell>Format</TableCell>
            <TableCell>File Size</TableCell>
            <TableCell>Audio</TableCell>
            <TableCell align="right">Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredFormats.map((format) => (
            <TableRow key={format.itag}>
              <TableCell>
                <Typography variant="body2">{format.quality}</Typography>
              </TableCell>
              <TableCell>
                <Typography variant="body2">
                  {format.mimeType.split(';')[0]}
                </Typography>
              </TableCell>
              <TableCell>
                <Typography variant="body2">
                  {formatFileSize(format.contentLength)}
                </Typography>
              </TableCell>
              <TableCell>
                <Typography variant="body2">
                  {format.hasAudio ? 'Yes' : 'No'}
                </Typography>
              </TableCell>
              <TableCell align="right">
                <Button
                  variant="contained"
                  size="small"
                  startIcon={<DownloadIcon />}
                  onClick={() => onDownload(format.itag)}
                  sx={{
                    bgcolor: 'primary.main',
                    '&:hover': {
                      bgcolor: 'primary.dark',
                    },
                  }}
                >
                  Download
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default DownloadOptions;
