import React, { useState } from 'react';
import { Container, Typography, Box, Alert, CircularProgress, Snackbar } from '@mui/material';
import { styled } from '@mui/material/styles';
import Header from './components/Header';
import Footer from './components/Footer';
import UrlInput from './components/UrlInput';
import VideoInfo from './components/VideoInfo';
import DownloadOptions from './components/DownloadOptions';
import { getVideoInfo, downloadVideo } from './utils/api';

const MainContent = styled(Box)({
  minHeight: '100vh',
  display: 'flex',
  flexDirection: 'column',
  background: 'linear-gradient(135deg, #17012B 0%, #1A0933 50%, #1F1147 100%)',
  color: 'white',
});

const HeroSection = styled(Box)({
  background: 'linear-gradient(135deg, rgba(26, 9, 51, 0.95) 0%, rgba(31, 17, 71, 0.95) 100%)',
  padding: '8rem 0 4rem 0',
  textAlign: 'center',
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'radial-gradient(circle at center, rgba(255, 71, 148, 0.15) 0%, transparent 70%)',
    pointerEvents: 'none',
  },
});

const ContentSection = styled(Container)({
  flex: 1,
  padding: '2rem 0',
  position: 'relative',
  zIndex: 1,
});

const GlowingTitle = styled(Typography)({
  fontSize: '3rem',
  fontWeight: 'bold',
  marginBottom: '1.5rem',
  background: 'linear-gradient(45deg, #ffffff 30%, #FF4794 90%)',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  textShadow: '0 0 30px rgba(255, 71, 148, 0.3)',
});

const Subtitle = styled(Typography)({
  color: 'rgba(255, 255, 255, 0.8)',
  fontSize: '1.2rem',
  maxWidth: '800px',
  margin: '0 auto 2rem',
});

function App() {
  const [videoInfo, setVideoInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [currentUrl, setCurrentUrl] = useState('');
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const handleUrlSubmit = async (url) => {
    setLoading(true);
    setError('');
    setVideoInfo(null);
    setCurrentUrl(url);
    setShowSuccessMessage(false);

    try {
      if (!url.trim()) {
        throw new Error('يرجى إدخال رابط فيديو يوتيوب');
      }

      try {
        new URL(url);
      } catch {
        throw new Error('يرجى إدخال رابط صحيح');
      }

      const info = await getVideoInfo(url);
      setVideoInfo(info);
      setShowSuccessMessage(true);
    } catch (err) {
      console.error('Error fetching video:', err);
      setError(err.message || 'فشل في جلب معلومات الفيديو. يرجى التحقق من الرابط والمحاولة مرة أخرى.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = (itag) => {
    try {
      downloadVideo(currentUrl, itag);
    } catch (err) {
      setError('فشل بدء التحميل. يرجى المحاولة مرة أخرى.');
    }
  };

  return (
    <MainContent>
      <Header />

      <HeroSection>
        <Container>
          <GlowingTitle variant="h1">
            حمّل فيديوهات يوتيوب بسهولة
          </GlowingTitle>
          <Subtitle variant="h5">
            قم بتحميل فيديوهات يوتيوب المفضلة لديك بجودة عالية وبصيغ مختلفة
          </Subtitle>
          <UrlInput onSubmit={handleUrlSubmit} />
        </Container>
      </HeroSection>

      <ContentSection>
        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress sx={{ color: '#FF4794' }} />
          </Box>
        )}

        {error && (
          <Alert 
            severity="error" 
            sx={{ 
              mb: 3, 
              backgroundColor: 'rgba(255, 71, 148, 0.1)', 
              color: '#FF4794',
              '& .MuiAlert-icon': { color: '#FF4794' }
            }}
            onClose={() => setError('')}
          >
            {error}
          </Alert>
        )}

        {videoInfo && (
          <>
            <VideoInfo videoInfo={videoInfo} />
            <DownloadOptions
              formats={videoInfo.formats}
              onDownload={handleDownload}
            />
          </>
        )}
      </ContentSection>

      <Footer />

      <Snackbar
        open={showSuccessMessage}
        autoHideDuration={3000}
        onClose={() => setShowSuccessMessage(false)}
        message="تم جلب معلومات الفيديو بنجاح"
        sx={{
          '& .MuiSnackbarContent-root': {
            backgroundColor: 'rgba(26, 9, 51, 0.95)',
            color: '#fff',
          }
        }}
      />
    </MainContent>
  );
}

export default App;
