import React from 'react';
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Box,
  Chip,
  Divider,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import PersonIcon from '@mui/icons-material/Person';
import VisibilityIcon from '@mui/icons-material/Visibility';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';

const GlassCard = styled(Card)({
  background: 'rgba(26, 26, 46, 0.6)',
  backdropFilter: 'blur(10px)',
  borderRadius: '15px',
  border: '1px solid rgba(255, 255, 255, 0.1)',
  color: 'white',
  boxShadow: '0 8px 32px 0 rgba(0, 0, 0, 0.37)',
  overflow: 'hidden',
  transition: 'transform 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
  },
});

const StyledCardMedia = styled(CardMedia)({
  height: '300px',
  position: 'relative',
  '&::after': {
    content: '""',
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    height: '100px',
    background: 'linear-gradient(to top, rgba(26, 26, 46, 1), transparent)',
  },
});

const InfoChips = styled(Box)({
  display: 'flex',
  gap: '1rem',
  flexWrap: 'wrap',
  marginBottom: '1rem',
});

const StyledChip = styled(Chip)({
  background: 'rgba(255, 255, 255, 0.1)',
  color: 'white',
  borderRadius: '25px',
  '& .MuiSvgIcon-root': {
    color: '#ff0000',
  },
});

const VideoInfo = ({ videoInfo }) => {
  if (!videoInfo) return null;

  const formatDuration = (duration) => {
    const match = duration.match(/PT(\d+H)?(\d+M)?(\d+S)?/);
    const hours = (match[1] || '').replace('H', '');
    const minutes = (match[2] || '').replace('M', '');
    const seconds = (match[3] || '').replace('S', '');

    let result = '';
    if (hours) result += `${hours}:`;
    result += `${minutes.padStart(2, '0')}:`;
    result += seconds.padStart(2, '0');
    return result;
  };

  const formatNumber = (num) => {
    return new Intl.NumberFormat('ar-EG').format(num);
  };

  return (
    <GlassCard sx={{ mb: 3 }}>
      <StyledCardMedia
        component="img"
        image={videoInfo.thumbnail}
        alt={videoInfo.title}
      />
      <CardContent sx={{ p: 3 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold' }}>
          {videoInfo.title}
        </Typography>

        <InfoChips>
          <StyledChip
            icon={<PersonIcon />}
            label={videoInfo.author}
          />
          <StyledChip
            icon={<AccessTimeIcon />}
            label={formatDuration(videoInfo.duration)}
          />
          <StyledChip
            icon={<VisibilityIcon />}
            label={`${formatNumber(videoInfo.viewCount)} مشاهدة`}
          />
          <StyledChip
            icon={<ThumbUpIcon />}
            label={`${formatNumber(videoInfo.likeCount)} إعجاب`}
          />
        </InfoChips>

        <Divider sx={{ my: 2, borderColor: 'rgba(255, 255, 255, 0.1)' }} />

        <Typography variant="body1" color="rgba(255, 255, 255, 0.7)" sx={{ mb: 2 }}>
          {videoInfo.description}
        </Typography>

        <Typography variant="body2" color="rgba(255, 255, 255, 0.5)">
          تاريخ النشر: {new Date(videoInfo.publishedAt).toLocaleDateString('ar-EG')}
        </Typography>
      </CardContent>
    </GlassCard>
  );
};

export default VideoInfo;
