const DownloadOptions = ({ formats, onDownload }) => {
  if (!formats || formats.length === 0) return null;

  const filteredFormats = formats.filter(
    format => format.hasVideo && format.quality
  );

  const isHighQuality = (quality) => {
    const qualityNumber = parseInt(quality.replace(/[^\d]/g, ''));
    return qualityNumber >= 1080;
  };

  return (
    <Box sx={{ mt: 4 }}>
      <TableTitle>
        اختر جودة التحميل المناسبة
        <StyledTooltip
          title={
            <React.Fragment>
              <Typography variant="subtitle2" sx={{ fontWeight: 'bold', mb: 1 }}>
                ملاحظة حول جودة الفيديو والصوت:
              </Typography>
              <Typography variant="body2">
                الفيديوهات بجودة 1080p وأعلى لا تحتوي على صوت مدمج. هذا لأن يوتيوب يفصل مسارات الفيديو عالية الجودة عن الصوت لتحسين الأداء.
              </Typography>
              <Typography variant="body2" sx={{ mt: 1 }}>
                للحصول على أفضل جودة، يمكنك:
                <br />
                1. تحميل الفيديو بالجودة المطلوبة
                <br />
                2. تحميل نسخة منفصلة بجودة أقل تحتوي على الصوت
              </Typography>
            </React.Fragment>
          }
        >
          <InfoIcon color="inherit" />
        </StyledTooltip>
      </TableTitle>

      <GlassTableContainer>
        <StyledTable>
          <TableHead>
            <TableRow>
              <TableCell>الجودة</TableCell>
              <TableCell>النوع</TableCell>
              <TableCell>حجم الملف</TableCell>
              <TableCell>
                الصوت
                {isHighQuality('1080p') && (
                  <StyledTooltip
                    title="الفيديوهات عالية الجودة لا تحتوي على صوت مدمج"
                  >
                    <InfoIcon color="inherit" />
                  </StyledTooltip>
                )}
              </TableCell>
              <TableCell align="right">التحميل</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredFormats.map((format) => (
              <TableRow key={format.itag}>
                <TableCell>
                  <QualityChip>
                    <IconWrapper>
                      <HdIcon />
                    </IconWrapper>
                    {format.quality}
                    {isHighQuality(format.quality) && !format.hasAudio && (
                      <StyledTooltip
                        title="هذه الجودة لا تحتوي على صوت مدمج. يمكنك تحميل نسخة منفصلة للصوت."
                      >
                        <InfoIcon color="inherit" />
                      </StyledTooltip>
                    )}
                  </QualityChip>
                </TableCell>
                <TableCell>
                  <QualityChip>
                    <IconWrapper>
                      <VideocamIcon />
                    </IconWrapper>
                    {format.mimeType.split(';')[0]}
                  </QualityChip>
                </TableCell>
                <TableCell>
                  <QualityChip>
                    <IconWrapper>
                      <DataUsageIcon />
                    </IconWrapper>
                    {formatFileSize(format.contentLength)}
                  </QualityChip>
                </TableCell>
                <TableCell>
                  <QualityChip>
                    <IconWrapper>
                      {format.hasAudio ? (
                        <VolumeUpIcon sx={{ 
                          color: 'white',
                          filter: 'drop-shadow(0 0 4px rgba(255, 255, 255, 0.4))'
                        }} />
                      ) : (
                        <VolumeOffIcon sx={{ 
                          color: 'rgba(255, 255, 255, 0.3)',
                          filter: 'none'
                        }} />
                      )}
                    </IconWrapper>
                    {format.hasAudio ? 'نعم' : 'لا'}
                  </QualityChip>
                </TableCell>
                <TableCell align="right">
                  <DownloadButton
                    variant="contained"
                    size="small"
                    startIcon={
                      <DownloadIcon sx={{ 
                        transition: 'all 0.3s ease',
                        '&:hover': {
                          transform: 'translateY(-2px)'
                        }
                      }} />
                    }
                    onClick={() => onDownload(format.itag)}
                  >
                    تحميل
                  </DownloadButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </StyledTable>
      </GlassTableContainer>
    </Box>
  );
};

