import React, { useState } from 'react';
import { TextField, Button, Box, Paper, InputAdornment, Tooltip } from '@mui/material';
import YouTubeIcon from '@mui/icons-material/YouTube';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';

const UrlInput = ({ onSubmit }) => {
  const [url, setUrl] = useState('');
  const [error, setError] = useState('');

  const validateUrl = (inputUrl) => {
    if (!inputUrl.trim()) {
      return 'Please enter a YouTube URL';
    }

    try {
      const urlObj = new URL(inputUrl);
      const isYouTube = ['youtube.com', 'www.youtube.com', 'youtu.be'].includes(urlObj.hostname);
      if (!isYouTube) {
        return 'Please enter a valid YouTube URL';
      }
    } catch {
      return 'Please enter a valid URL';
    }

    return '';
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationError = validateUrl(url);
    setError(validationError);

    if (!validationError) {
      onSubmit(url);
    }
  };

  const handleUrlChange = (e) => {
    const newUrl = e.target.value;
    setUrl(newUrl);
    if (error) {
      setError('');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
      <form onSubmit={handleSubmit}>
        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <TextField
            fullWidth
            variant="outlined"
            label="YouTube URL"
            placeholder="https://www.youtube.com/watch?v=..."
            value={url}
            onChange={handleUrlChange}
            error={!!error}
            helperText={error}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <YouTubeIcon sx={{ color: 'red' }} />
                </InputAdornment>
              ),
              endAdornment: (
                <InputAdornment position="end">
                  <Tooltip title="Enter a YouTube video URL. Supported formats: youtube.com/watch?v=... or youtu.be/...">
                    <HelpOutlineIcon sx={{ color: 'action.active', cursor: 'help' }} />
                  </Tooltip>
                </InputAdornment>
              ),
            }}
          />
          <Button
            variant="contained"
            type="submit"
            size="large"
            sx={{
              bgcolor: 'red',
              '&:hover': {
                bgcolor: '#c62828',
              },
              minWidth: '120px',
            }}
          >
            Get Video
          </Button>
        </Box>
      </form>
    </Paper>
  );
};

export default UrlInput;
