import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const getVideoInfo = async (url) => {
  try {
    const response = await axios.get(`${API_URL}/video-info`, {
      params: { url }
    });
    return response.data;
  } catch (error) {
    throw new Error(error.response?.data?.error || 'Failed to fetch video info');
  }
};

export const downloadVideo = (url, itag) => {
  const downloadUrl = `${API_URL}/download?url=${encodeURIComponent(url)}&itag=${itag}`;
  window.location.href = downloadUrl;
};
