import axios from 'axios';

// Create axios instance with default config
const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  timeout: 30000, // 30 seconds timeout
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

// Add request interceptor for logging
api.interceptors.request.use(
  config => {
    console.log('API Request:', config.method.toUpperCase(), config.url);
    return config;
  },
  error => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for error handling
api.interceptors.response.use(
  response => {
    console.log('API Response:', response.status);
    return response;
  },
  error => {
    console.error('API Response Error:', error);
    if (error.code === 'ERR_NETWORK') {
      throw new Error('لا يمكن الاتصال بالخادم. يرجى التأكد من تشغيل الخادم والمحاولة مرة أخرى');
    }
    throw error.response?.data?.error || error.message || 'حدث خطأ غير معروف';
  }
);

export const getVideoInfo = async (url) => {
  try {
    const response = await fetch(`${API_BASE_URL}/info?url=${encodeURIComponent(url)}`);
    if (!response.ok) {
      throw new Error('فشل في جلب معلومات الفيديو');
    }
    return await response.json();
  } catch (error) {
    throw new Error('حدث خطأ أثناء جلب معلومات الفيديو');
  }
};

export const downloadVideo = (url, itag, fileName) => {
  const downloadUrl = `${API_BASE_URL}/download?url=${encodeURIComponent(url)}&itag=${itag}`;

  // إنشاء رابط مؤقت للتحميل
  const link = document.createElement('a');
  link.href = downloadUrl;
  link.download = fileName || 'download.mp4'; // استخدام اسم الملف المقترح أو الاسم الافتراضي
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};
